package cn.com.phinfo.adapter;import cn.com.phinfo.adapter.base.FriendsBaseAdapter;import cn.com.phinfo.protocol.LURLInterface;import cn.com.phinfo.protocol.UnitandaddressRun.UnitandaddressItem;public class AddressFridesBaseAdapter extends FriendsBaseAdapter<UnitandaddressItem>{	@Override	protected void onSetData(cn.com.phinfo.adapter.base.FriendsBaseAdapter.Holder holder, UnitandaddressItem it) {		if(!it.getIsDir()){			this.getAsyncAvatar(holder.home_icon, LURLInterface.GET_AVATAR(it.getUserId()), it.GET_NAME_DRAWABLE());			holder.item_text.setText(it.getMobile());		}	}}