package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.AttendDetailRun.AttendDetailItem;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;public class AttendDetailAdapter extends MyImgAdapterBaseAbs<AttendDetailItem>{	private String oldDate;	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_attenddetail_item, null);			hoder.date = (TextView) arg1.findViewById(R.id.date);			hoder.title = (TextView) arg1.findViewById(R.id.title);			hoder.name = (TextView) arg1.findViewById(R.id.name);			hoder.address = (TextView) arg1.findViewById(R.id.address);			hoder.name = (TextView) arg1.findViewById(R.id.name);			hoder.AttendName = (TextView) arg1.findViewById(R.id.AttendName);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		AttendDetailItem it = this.getItem(arg0);				if(arg0==0){			oldDate = it.getAttendDate2YM();			hoder.date.setVisibility(View.VISIBLE);			hoder.date.setText(oldDate);		}		else{			if(it.getAttendDate2YM().equals(oldDate)){				hoder.date.setVisibility(View.GONE);			}else{				oldDate = it.getAttendDate2YM();				hoder.date.setVisibility(View.VISIBLE);				hoder.date.setText(oldDate);							}		}		hoder.title.setText(it.getCheckTime());		hoder.name.setText(it.getBuildingName());		hoder.address.setText(it.getLocation());		hoder.AttendName.setText("拜访对象:"+it.getContactName());		return arg1;	}		class Holder{		public TextView title,name,address,AttendName,date;	}}