package cn.com.phinfo.adapter;import java.util.Comparator;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import cn.com.phinfo.entity.ExPoiInfo;import cn.com.phinfo.oaact.R;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;public class BuilderBaiduMapAdapter extends MyAdapterBaseAbs<ExPoiInfo> {	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_build_baidumap_item, null);			hoder.name = (TextView) arg1					.findViewById(R.id.adapter_baidumap_location_name);			hoder.address = (TextView) arg1					.findViewById(R.id.adapter_baidumap_location_address);			hoder.checked = (ImageView) arg1					.findViewById(R.id.adapter_baidumap_location_checked);			hoder.size =  (TextView)arg1.findViewById(R.id.adapter_baidumap_location_size);			arg1.setTag(hoder);		} else {			hoder = (Holder) arg1.getTag();		}		ExPoiInfo poiInfo = this.getItem(arg0);		if(poiInfo.getSelect()){			hoder.checked.setVisibility(View.VISIBLE);		}else{			hoder.checked.setVisibility(View.INVISIBLE);		}		if(arg0 == 0){			hoder.name.setText("【当前】"+poiInfo.name);		}else{			hoder.name.setText(poiInfo.name);		}		hoder.address.setText(poiInfo.address);		hoder.size.setText("距离"+String.format("%.2f", poiInfo.getSize())+"米");		return arg1;	}	public void sort(){		super.sort(new Comparator<ExPoiInfo>(){			@Override			public int compare(ExPoiInfo arg0, ExPoiInfo arg1) {				return (int) (arg0.getSize()-arg1.getSize());			}		});	}	class Holder {		public TextView name, address,size;		public ImageView checked;	}}