package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.CommentListRun.CommentItem;import cn.com.phinfo.protocol.LURLInterface;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;import com.heqifuhou.view.CircleImageView;public class CommentAdapter extends MyImgAdapterBaseAbs<CommentItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_comment_item, null);			hoder.date = (TextView) arg1.findViewById(R.id.date);			hoder.count = (TextView) arg1.findViewById(R.id.count);			hoder.comment = (TextView) arg1.findViewById(R.id.comment);			hoder.like = (TextView) arg1.findViewById(R.id.like);			hoder.name = (TextView) arg1.findViewById(R.id.name);			hoder.icon_avatar = (CircleImageView) arg1.findViewById(R.id.icon_avatar);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		CommentItem it = this.getItem(arg0);		hoder.date.setText(it.getCreatedon());		hoder.count.setText("0"+"条评论");		hoder.comment.setText(it.getComment());		hoder.like.setText("0");		hoder.name.setText(it.getCreatedbyname());		this.getAsyncAvatar(hoder.icon_avatar, LURLInterface.GET_AVATAR(it.getCreatedby()), it.getCreatedbyname());		return arg1;	}	class Holder{		public CircleImageView icon_avatar;		public TextView date,count,comment,like,name;	}}