package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import cn.com.phinfo.entity.FilePickerItem;import cn.com.phinfo.oaact.R;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;public class FilePickerAdapter extends MyAdapterBaseAbs<FilePickerItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_filepick_item, null);			hoder.text = (TextView) arg1.findViewById(R.id.text);			hoder.del_btn = (ImageView) arg1.findViewById(R.id.del_btn);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		FilePickerItem it = this.getItem(arg0);		hoder.text.setText(it.getLocalName());		hoder.del_btn.setTag(it);		hoder.del_btn.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View arg0) {				FilePickerItem itFile= (FilePickerItem) arg0.getTag();				delItem(itFile);			}		});		return arg1;	}		private void delItem(FilePickerItem it){		for(int i=0;i<this.getCount();i++){			FilePickerItem _it = this.getItem(i);			if(_it.getGuid().equals(it.getGuid())){				remove(i);				break;			}		}	}		class Holder{		public TextView text;		public ImageView del_btn;	}}