package cn.com.phinfo.adapter;import android.view.View;import cn.com.phinfo.adapter.base.FriendsBaseAdapter;import cn.com.phinfo.protocol.LURLInterface;import cn.com.phinfo.protocol.UnitandaddressRun.UnitandaddressItem;public class GroupUserAdapter extends FriendsBaseAdapter<UnitandaddressItem>{	@Override	protected void onSetData(cn.com.phinfo.adapter.base.FriendsBaseAdapter.Holder holder, UnitandaddressItem it) {		this.getAsyncAvatar(holder.home_icon, LURLInterface.GET_AVATAR(it.getUserId()), it.GET_NAME_DRAWABLE());		holder.stateLi.setVisibility(View.GONE);	}}