package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import cn.com.phinfo.entity.HomeTab1Item;import cn.com.phinfo.oaact.R;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;public class HomeTab1Adapter extends MyAdapterBaseAbs<HomeTab1Item>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_home_tab1_item, null);			hoder.title = (TextView) arg1.findViewById(R.id.title);			hoder.des = (TextView) arg1.findViewById(R.id.des);			hoder.icon = (ImageView) arg1.findViewById(R.id.icon);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		HomeTab1Item it = this.getItem(arg0);		hoder.des.setText(it.getDes());		hoder.title.setText(it.getText());		hoder.icon.setImageResource(it.getnResID());		return arg1;	}	class Holder{		public TextView des,title;		public ImageView icon;	}}