package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.LURLInterface;import cn.com.phinfo.protocol.LikeListRun.LikeItem;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;public class LikeAdapter extends  MyImgAdapterBaseAbs<LikeItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_like_item, null);			hoder.photo = (ImageView) arg1.findViewById(R.id.photo);			hoder.Name = (TextView) arg1.findViewById(R.id.Name);			hoder.date = (TextView) arg1.findViewById(R.id.date);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		LikeItem it = this.getItem(arg0);		hoder.Name.setText(it.getCreatedByName());		hoder.date.setText(it.getCreatedOn());		this.getAsyncAvatar(hoder.photo,LURLInterface.GET_AVATAR(it.getCreatedBy()),it.getCreatedByName());		return arg1;	}	class Holder{		public TextView Name,date;		public ImageView photo;	}}