package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.MeetingItemRun.MeetingItem;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;import com.heqifuhou.view.CircleImageView;public class MeetingItemAdapter extends MyImgAdapterBaseAbs<MeetingItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_meeting_comment_item, null);			hoder.date = (TextView) arg1.findViewById(R.id.date);			hoder.name = (TextView) arg1.findViewById(R.id.name);			hoder.icon_avatar = (CircleImageView) arg1.findViewById(R.id.icon_avatar);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		MeetingItem it = this.getItem(arg0);		hoder.date.setText(it.getModifiedOn());		hoder.name.setText(it.getDescription());		return arg1;	}	class Holder{		public CircleImageView icon_avatar;		public TextView date,name;	}}