package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.LURLInterface;import cn.com.phinfo.protocol.PeoplesListRun.PeopLesItem;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;import com.heqifuhou.view.CircleImageView;public class MeetingPeoplesAdapter extends MyImgAdapterBaseAbs<PeopLesItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_meetingpeoples_item, null);			hoder.date = (TextView) arg1.findViewById(R.id.date);			hoder.title = (TextView) arg1.findViewById(R.id.title);			hoder.icon_avatar = (CircleImageView) arg1.findViewById(R.id.icon_avatar);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		PeopLesItem it = this.getItem(arg0);		hoder.date.setText(it.getModifiedOn());		hoder.title.setText(it.getOwningUserName());     	this.getAsyncAvatar(hoder.icon_avatar,  LURLInterface.GET_AVATAR(it.getOwningUser()),it.getOwningUserName());		return arg1;	}	class Holder{		public CircleImageView icon_avatar;		public TextView date,title;	}}