package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.LURLInterface;import cn.com.phinfo.protocol.MeetingRecListRun.MeetingRecListItem;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;import com.heqifuhou.view.CircleImageView;public class MeetingRecListAdapter extends MyImgAdapterBaseAbs<MeetingRecListItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_meeting_reclist_item, null);			hoder.begin = (TextView) arg1.findViewById(R.id.begin);			hoder.end = (TextView) arg1.findViewById(R.id.end);			hoder.title = (TextView) arg1.findViewById(R.id.title);			hoder.name = (TextView) arg1.findViewById(R.id.name);			hoder.icon_avatar = (CircleImageView) arg1.findViewById(R.id.icon_avatar);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		MeetingRecListItem it = this.getItem(arg0);		hoder.begin.setText(it.getScheduledStart2HM());		hoder.end.setText(it.getScheduledEnd2HM());		hoder.title.setText(it.getName());		hoder.name.setText(it.getMeetingContent());		this.getAsyncAvatar(hoder.icon_avatar, LURLInterface.GET_AVATAR(it.getCreatedBy()), it.getName());		return arg1;	}	class Holder{		public CircleImageView icon_avatar;		public TextView begin,end,title,name;	}}