package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.SearchReportRun.ImgItem;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;import com.heqifuhou.utils.ParamsCheckUtils;public class MyQThumbnailAdapter extends MyImgAdapterBaseAbs<String>{	private static class Holder{		ImageView item_grida_image;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		Holder holder = null;		if (convertView == null) {			convertView = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_thumbnail_grida,					null);			holder = new Holder();			holder.item_grida_image = (ImageView) convertView.findViewById(R.id.item_grida_image);			holder.item_grida_image.setLongClickable(false);			convertView.setTag(holder);		} else {			holder = (Holder) convertView.getTag();		}		String picUrl = this.getItem(position);		if(!ParamsCheckUtils.isNull(picUrl)&&!picUrl.startsWith("http:")){			this.getAsyncBitMap(holder.item_grida_image, "file:///"+picUrl,R.drawable.default_image_layer);		}else{			this.getAsyncBitMap(holder.item_grida_image, picUrl,R.drawable.default_image_layer);		}		return convertView;	}}