package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.NewsChannelRun.HSelectItem;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;public class NewsChanelAdapter extends MyAdapterBaseAbs<HSelectItem> {	@Override	public View getView(int arg0, View convertView, ViewGroup arg2) {		Holder holder = null;		if (convertView == null) {			holder  = new Holder();			convertView = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_add_news_item, null);			holder.newDel = convertView.findViewById(R.id.newDel);			holder.newDel.setVisibility(View.INVISIBLE);			holder.newTitle = (TextView) convertView.findViewById(R.id.newTitle);			holder.convertView= convertView;			convertView.setTag(holder);		} else {			holder =  (Holder) convertView.getTag();		}		HSelectItem it = this.getItem(arg0);		holder.newTitle.setText(it.getName());		return convertView;	}	private static class Holder{		private TextView newTitle;		private View newDel;		private View convertView;	}}