package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.NewsDefaultListRun.NewsItem;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;public class NewsOtherAdapter extends MyAdapterBaseAbs<NewsItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_news_other_item, null);			hoder.text = (TextView) arg1.findViewById(R.id.text);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		NewsItem it = this.getItem(arg0);		hoder.text.setText(it.getTitle());		return arg1;	}	class Holder{		public TextView text;	}}