package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import cn.com.phinfo.adapter.base.FriendsBaseAdapter;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.UnitandaddressRun.UnitandaddressItem;public class SelectPersonAdapter extends FriendsBaseAdapter<UnitandaddressItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		UnitandaddressItem it = this.getItem(arg0);		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			if(it.getIsDir()){				arg1 = LayoutInflater.from(arg2.getContext()).inflate(						R.layout.adapter_dir_item, null);				hoder.item_title = (TextView) arg1.findViewById(R.id.item_title);			}else{				arg1 = LayoutInflater.from(arg2.getContext()).inflate(						R.layout.adapter_select_person_item, null);				hoder.tvLetter = (TextView) arg1.findViewById(R.id.catalog);				hoder.title = (TextView) arg1.findViewById(R.id.title);				hoder.name = (TextView) arg1.findViewById(R.id.name);				hoder.pic = (ImageView) arg1.findViewById(R.id.pic);			}			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		if(!it.getIsDir()){			hoder.name.setText(it.GET_USER_NAME());			if(!it.getIsbSel()){				hoder.pic.setImageResource(R.drawable.tick_n);			}else{				hoder.pic.setImageResource(R.drawable.tick_hover);			}			int section = getSectionForPosition(arg0);			if (arg0 == getPositionForSection(section)) {				hoder.tvLetter.setVisibility(View.VISIBLE);				hoder.tvLetter.setText(it.getSortLetters());			} else {				hoder.tvLetter.setVisibility(View.GONE);			}		}else{			hoder.item_title.setText(it.getParentBusinessUnitId());		}		return arg1;	}	public String getUserAllIds(){		StringBuilder sb = new StringBuilder();		for(int i=0;i<this.getCount();i++){			UnitandaddressItem it = this.getItem(i);			if(it.getIsbSel()){				if(sb.length()>0){					sb.append(",");				}				sb.append(it.getUserId());				}		}		return sb.toString();	}	class Holder{		public ImageView pic;		public TextView name,title,tvLetter,item_title;	}	@Override	protected void onSetData(cn.com.phinfo.adapter.base.FriendsBaseAdapter.Holder holder, UnitandaddressItem it) {		}}