package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.db.SettingDB;import cn.com.phinfo.entity.IPListItem;import cn.com.phinfo.oaact.R;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;import com.heqifuhou.utils.ParamsCheckUtils;public class SettingAdapter extends MyAdapterBaseAbs<IPListItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_setting_item, null);			hoder.text = (TextView) arg1.findViewById(R.id.text);			hoder.sel = arg1.findViewById(R.id.sel);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		IPListItem it = this.getItem(arg0);		if(ParamsCheckUtils.isNull(it.getName())){			hoder.text.setText(it.getIp());		}else{			hoder.text.setText(it.getName());		}		IPListItem selIt = SettingDB.getInstance().getCurrFromDB();		if(!ParamsCheckUtils.isNull(selIt.getIp())&&selIt.getIp().equals(it.getIp())){			hoder.sel.setVisibility(View.VISIBLE);		}else{			hoder.sel.setVisibility(View.INVISIBLE);		}		return arg1;	}	public void delItem(IPListItem it){		for(int i=0;i<this.getCount();i++){			IPListItem _it = this.getItem(i);			if(_it.getIp().equals(it.getIp())){				remove(i);				break;			}		}	}	class Holder{		public TextView text;		public View sel;	}}