package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.LURLInterface;import cn.com.phinfo.protocol.LikeListRun.LikeItem;import cn.com.phinfo.protocol.SearchChatterRun.PollOption;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;public class ShareVoteAdapter extends  MyImgAdapterBaseAbs<PollOption>{	private boolean bShow = true;	public ShareVoteAdapter(boolean bShow){		this.bShow = bShow;	}	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_poll_item, null);			hoder.pollTick = (ImageView) arg1.findViewById(R.id.pollTick);			hoder.pollTitle = (TextView) arg1.findViewById(R.id.pollTitle);			hoder.peopCount = (TextView) arg1.findViewById(R.id.peopCount);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		PollOption it = this.getItem(arg0);		hoder.pollTitle.setText(it.getOption());		hoder.peopCount.setText(it.getPollQty());		if(this.bShow){			hoder.pollTick.setVisibility(View.VISIBLE);			if(it.getIsPolled()){				hoder.pollTick.setImageResource(R.drawable.tick_hover);				arg1.setBackgroundColor(0xfff7d6ad);			}else{				hoder.pollTick.setImageResource(R.drawable.tick_n);				arg1.setBackgroundColor(0x00000000);			}		}else{			hoder.pollTick.setVisibility(View.GONE);		}		return arg1;	}		public void setSel(PollOption it,boolean bMu){		if(!bMu){			for(PollOption o:this.getListRef()){				o.setIsPolled(false);			}		}		it.setIsPolled(true);		notifyDataSetChanged();	}	class Holder{		public TextView pollTitle,peopCount;		public ImageView pollTick;	}}