package cn.com.phinfo.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import cn.com.phinfo.oaact.R;import cn.com.phinfo.protocol.LURLInterface;import cn.com.phinfo.protocol.TodosRun.TodosItem;import com.heqifuhou.adapterbase.MyImgAdapterBaseAbs;public class TodosAdapter extends MyImgAdapterBaseAbs<TodosItem>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder hoder = null;		if (arg1 == null) {			hoder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_todos_item, null);			hoder.name = (TextView) arg1.findViewById(R.id.name);			hoder.title = (TextView) arg1.findViewById(R.id.title);			hoder.date = (TextView) arg1.findViewById(R.id.date);			hoder.depament = (TextView) arg1.findViewById(R.id.depament);			hoder.tag = (ImageView) arg1.findViewById(R.id.tag);			hoder.iconAvatar  = (ImageView) arg1.findViewById(R.id.iconAvatar);			arg1.setTag(hoder);		}else{			hoder = (Holder) arg1.getTag();		}		TodosItem it = this.getItem(arg0);		hoder.name.setText(it.getCreatedByName());		hoder.date.setText(it.getCreatedOn());		hoder.title.setText(it.getName());		hoder.depament.setText(it.getBusinessUnitIdName());		if(it.getPriority().equals("0")){			hoder.tag.setImageResource(R.drawable.daiban_icon_pu);		}else if(it.getPriority().equals("1")){			hoder.tag.setImageResource(R.drawable.daiban_icon_te);		}else if(it.getPriority().equals("2")){			hoder.tag.setImageResource(R.drawable.daiban_icon_ji);		}		this.getAsyncAvatar(hoder.iconAvatar, LURLInterface.GET_AVATAR(it.getCreatedBy()),it.getCreatedByName());		return arg1;	}	public void delByID(String id){		for(int i=0;i<this.getCount();i++){			TodosItem it = this.getItem(i);			if(it.getId().equals(id)){				this.remove(i);				notifyDataSetChanged();				break;			}		}	}	class Holder{		public TextView title,date,name,depament;		public ImageView tag,iconAvatar;	}}