package cn.com.phinfo.adapter.base;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;public abstract class DepartmentBaseAdapter<E> extends MyAdapterBaseAbs<E>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder holder = null;		if (arg1 == null) {			holder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_deparment_item, null);			holder.item_title = (TextView) arg1.findViewById(R.id.item_title);			arg1.setTag(holder);		}else{			holder = (Holder) arg1.getTag();		}		E it = this.getItem(arg0);		holder.item_title.setText(onGetText(it));		return arg1;	}	protected abstract String onGetText(E it);		public static class Holder{		public TextView item_title;	}}