package cn.com.phinfo.adapter.base;import com.heqifuhou.adapterbase.MyAdapterBaseAbs;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import cn.com.phinfo.oaact.R;public abstract class SelectBaseAdapter<E> extends MyAdapterBaseAbs<E>{	@Override	public View getView(int arg0, View arg1, ViewGroup arg2) {		Holder holder = null;		if (arg1 == null) {			holder = new Holder();			arg1 = LayoutInflater.from(arg2.getContext()).inflate(					R.layout.adapter_select_item, null);			holder.text = (TextView) arg1.findViewById(R.id.text);			arg1.setTag(holder);		}else{			holder = (Holder) arg1.getTag();		}		E it = this.getItem(arg0);		holder.text.setText(onGetText(it));		return arg1;	}	protected abstract String onGetText(E it);	public static class Holder{		public TextView text;	}}