package cn.com.phinfo.oaact;import android.content.Intent;import android.os.Bundle;import cn.com.phinfo.entity.DataInstance;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.utils.ParamsCheckUtils;import com.heqifuhou.zxing.CaptureActivity;//二维码扫码public class HomeCaptureAct extends CaptureActivity{	private String code = "";	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);	}	protected  void onCaptureCodeEvent(String code)	{		this.code = code;		if(ParamsCheckUtils.isNull(code)){			this.showToast("内容为空");			this.finish();			return;		}		//218.241.208.171:9009/_ui/meeting/checkin?method=meeting.attend.checkin&id=		String token = DataInstance.getInstance().getToken();		String url = this.code+ "&sessionKey="+token;		Intent i = new Intent(this,WebViewRefreshAct.class);		i.putExtra("URL",url);		i.putExtra("TITLE", "二维码的结果");		startActivity(i);		this.finish();	}	@Override	protected void onHttpResult(int id, HttpResultBeanBase obj,Object requestObj) {		if(id == 0x10){		}		this.finish();	}}