package cn.com.phinfo.protocol;import java.util.List;import java.util.Stack;import com.alibaba.fastjson.annotation.JSONField;import com.alibaba.fastjson.annotation.JSONType;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;public class BaseInfoRun extends QuickRunObjectBase {	public BaseInfoRun(final String id) {		super(LURLInterface.GET_URL_NEWS_basicinfo(id),null,BaseInfoResultBean.class);	}	public static class BaseInfoResultBean extends HttpResultBeanBase{		private List<BaseInfoItem> data = new Stack<BaseInfoItem>();		public List<BaseInfoItem> getData() {			return data;		}		public void setData(List<BaseInfoItem> data) {			this.data = data;		}		@JSONField(serialize=false)		public BaseInfoItem getBaseInfoItem(){			if(this.data.isEmpty()){				return null;			}			return this.data.get(0);		}	}	public static class BaseInfoItem{		private String contentid;		private String title;		private String keywords;		private String folderid;		private int readcount;		private int commentcount;		private int likecount;		private String businessunitid;		private String createdby;		private String createdon;		private String businessunitidname;		public String getContentid() {			return contentid;		}		public void setContentid(String contentid) {			this.contentid = contentid;		}		public String getTitle() {			return title;		}		public void setTitle(String title) {			this.title = title;		}		public String getKeywords() {			return keywords;		}		public void setKeywords(String keywords) {			this.keywords = keywords;		}		public String getFolderid() {			return folderid;		}		public void setFolderid(String folderid) {			this.folderid = folderid;		}		public int getReadcount() {			return readcount;		}		public void setReadcount(int readcount) {			this.readcount = readcount;		}		public int getCommentcount() {			return commentcount;		}		public void setCommentcount(int commentcount) {			this.commentcount = commentcount;		}		public int getLikecount() {			return likecount;		}		public void setLikecount(int likecount) {			this.likecount = likecount;		}		public String getBusinessunitid() {			return businessunitid;		}		public void setBusinessunitid(String businessunitid) {			this.businessunitid = businessunitid;		}		public String getCreatedby() {			return createdby;		}		public void setCreatedby(String createdby) {			this.createdby = createdby;		}		public String getCreatedon() {			return createdon;		}		public void setCreatedon(String createdon) {			this.createdon = createdon;		}		public String getBusinessunitidname() {			return businessunitidname;		}		public void setBusinessunitidname(String businessunitidname) {			this.businessunitidname = businessunitidname;		}	}}