package cn.com.phinfo.protocol;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Arrays;import java.util.Date;import java.util.List;import java.util.Stack;import cn.com.phinfo.protocol.UnitandaddressRun.UnitandaddressItem;import com.alibaba.fastjson.annotation.JSONField;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;import com.heqifuhou.utils.ParamsCheckUtils;//获取单个会议室预约信息public class EntityInfoRun extends QuickRunObjectBase {	public EntityInfoRun(final String Id) {		super(LURLInterface.GET_URL_ENTITY_INFO_GET(String.valueOf(20035),Id),null,EntityInfoResultBean.class);	}	public static class EntityInfoResultBean extends HttpResultBeanBase{		private EntityInfoItem data = new EntityInfoItem();		public EntityInfoItem getData() {			return data;		}		public void setData(EntityInfoItem data) {			this.data = data;		}	}		public static class EntityInfoItem{		private String id;//":"92dac8e9-522e-4c79-8dc9-545929c059dd","		private String resourceId;//":"f5decd73-053c-45ad-a50f-e28bf86ea003","		private String resourceIdName;//":"第一会议室","		private String scheduledStart;//":"2017/4/5 16:01:00","		private String scheduledEnd;//":"2017/4/5 18:01:00","		private String subject;//":"舞蹈队排练","		private String description;//":"","		private String stateName;//":"审批通过","		private String owningUserName;//":"魏欣欣","		private String stateCode;//":"1","		private String attachs;//":"投影,话筒,电脑","		private String owningUser;//":"295c23e3-f16c-4b34-825f-4d22fcfb0470","		private List<ApproversItem> approvers = new Stack<ApproversItem>();//":[{"approverId":"69cc083a-ef76-4821-a92f-4bbe0d4b343b","approverName":"潘腾"}]		public String getId() {			return id;		}		public void setId(String id) {			this.id = id;		}		public String getResourceId() {			return resourceId;		}		public void setResourceId(String resourceId) {			this.resourceId = resourceId;		}		public String getResourceIdName() {			return resourceIdName;		}		public void setResourceIdName(String resourceIdName) {			this.resourceIdName = resourceIdName;		}		public String getScheduledStart() {			return getScheduled2F(scheduledStart);		}		@JSONField(serialize=false)		public String getScheduledStart2Format() {			return getScheduled2Format(scheduledStart);		}				public void setScheduledStart(String scheduledStart) {			this.scheduledStart = scheduledStart;		}		@JSONField(serialize=false)		public String getScheduledEnd2Format() {			return getScheduled2Format(scheduledEnd);		}		public String getScheduledEnd() {			return getScheduled2F(scheduledEnd);		}		@JSONField(serialize=false)		private static String getScheduled2F(String s) {			SimpleDateFormat formatter = new SimpleDateFormat("yyyy/M/d HH:mm:ss");			try {				Date d = formatter.parse(s);				formatter = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");				return formatter.format(d);			} catch (ParseException e) {			}			return s;		}		@JSONField(serialize=false)		private static String getScheduled2Format(String s) {			SimpleDateFormat formatter = new SimpleDateFormat("yyyy/MM/dd HH:mm:ss");			try {				Date d = formatter.parse(s);				formatter = new SimpleDateFormat("yyyyMMddHHmm");				return formatter.format(d);			} catch (ParseException e) {			}			return s;		}				public void setScheduledEnd(String scheduledEnd) {			this.scheduledEnd = scheduledEnd;		}		public String getSubject() {			return subject;		}		public void setSubject(String subject) {			this.subject = subject;		}		public String getDescription() {			return description;		}		public void setDescription(String description) {			this.description = description;		}		public String getStateName() {			return stateName;		}		public void setStateName(String stateName) {			this.stateName = stateName;		}		public String getOwningUserName() {			return owningUserName;		}		public void setOwningUserName(String owningUserName) {			this.owningUserName = owningUserName;		}		public String getStateCode() {			return stateCode;		}		public void setStateCode(String stateCode) {			this.stateCode = stateCode;		}		public String getAttachs() {			return attachs;		}		@JSONField(serialize=false)		public List<String> getAttache2List(){			if(ParamsCheckUtils.isNull(attachs)){				return new Stack<String>();			}			String[] s = attachs.split(",");			return Arrays.asList(s);		}		public void setAttachs(String attachs) {			this.attachs = attachs;		}		public String getOwningUser() {			return owningUser;		}		public void setOwningUser(String owningUser) {			this.owningUser = owningUser;		}		public List<ApproversItem> getApprovers() {			return approvers;		}		public void setApprovers(List<ApproversItem> approvers) {			this.approvers = approvers;		}		@JSONField(serialize=false)		public List<UnitandaddressItem> getApprovers2List(){			List<UnitandaddressItem> lst = new Stack<UnitandaddressItem>();			for(ApproversItem it:approvers){				lst.add(it.get2UnitandaddressItem());			}			return lst;		}			}	public static class ApproversItem{		private String approverId;		private String approverName;		public String getApproverId() {			return approverId;		}		public void setApproverId(String approverId) {			this.approverId = approverId;		}		public String getApproverName() {			return approverName;		}		public void setApproverName(String approverName) {			this.approverName = approverName;		}		@JSONField(serialize=false)		public UnitandaddressItem get2UnitandaddressItem(){			UnitandaddressItem it = new UnitandaddressItem();			it.setSystemUserId(this.getApproverId());			it.setFullName(this.getApproverName());			return it;		}	}}