package cn.com.phinfo.protocol;import java.util.HashMap;import cn.com.phinfo.protocol.AttentdsettingsRun.GlobalSettingsItem;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;public class HR_AttendsettingsRun extends QuickRunObjectBase {	public HR_AttendsettingsRun(final GlobalSettingsItem globalSettingsItem) {		super(LURLInterface.GET_HR_ATTENDSETTINGS_SAVE(),new HashMap<String,Object>(){			{				put("FlexTime",globalSettingsItem.getFlexTime());				put("LateTime",globalSettingsItem.getLateTime());				put("AbsenceTime",globalSettingsItem.getAbsenceTime());				put("RemindCheckIn",globalSettingsItem.getRemindCheckIn());				put("RemindCheckOut",globalSettingsItem.getRemindCheckOut());				put("AdvancedCheckIn",globalSettingsItem.getAdvancedCheckIn());				put("RemindLeaveWorkCheck",globalSettingsItem.getRemindLeaveWorkCheck()?"true":"false");				put("NeedPhoto",globalSettingsItem.getNeedPhoto()?"true":"false");				put("FastCheckIn",globalSettingsItem.getFastCheckIn()?"true":"false");				put("DistanceRange",String.valueOf(globalSettingsItem.getDistanceRange()));			}		},AttendsettingsResultBean.class);	}		public static class AttendsettingsResultBean extends HttpResultBeanBase{			}		}