package cn.com.phinfo.protocol;import java.util.HashMap;import cn.com.phinfo.protocol.MeetingItemRun.MeetingItem;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;public class MeetingItemAddRun extends QuickRunObjectBase {	public MeetingItemAddRun(final String meetingId,final String name,final String description) {		super(LURLInterface.GET_URL_MEETING_ITEM_ADD(meetingId),new HashMap<String,Object>(){			{				put("name",name);				put("description",description);			}		},MeetingItemResultBean.class);	}	public static class MeetingItemResultBean extends HttpResultBeanBase{		private MeetingItem data = new MeetingItem();		public MeetingItem getData() {			return data;		}		public void setData(MeetingItem data) {			this.data = data;		}	}}