package cn.com.phinfo.protocol;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;import java.util.List;import java.util.Stack;import com.alibaba.fastjson.annotation.JSONField;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;//显示状态public class MettingRecRun extends QuickRunObjectBase {	public MettingRecRun(final String startTime,final String endTime){		this(startTime,endTime,"");	}	public MettingRecRun(final String startTime,final String endTime,final String search) {		super(LURLInterface.GET_URL_MEETING_REC(startTime,endTime,search),null,MettingRecResultBean.class);	}	public static class MettingRecResultBean extends HttpResultBeanBase{		private List<MeetingRecItem> listData = new Stack<MeetingRecItem>();		public List<MeetingRecItem> getListData() {			return listData;		}		public void setListData(List<MeetingRecItem> listData) {			this.listData = listData;		}	}		public static class MeetingRecItem{		private String ValueId;		private String Name;//":"关于OA使用凤凰世纪办公系统","		private String MeetingType;//":"0","		private String ScheduledStart;//":"2017/3/25 8:00:00","		private String ScheduledEnd;//":"2017/3/25 10:30:00","		private String RimderTime;//":"","		private String RoomId;//":"","		private String Location;//":"关于OA使用","		private String InviteQty;//":"","		private String JoinQty;//":"","		private String StatusCode;//":"1","		private String HostName;//":"","		private String ContactUser;//":"8ceff7aa-f691-48dc-8d89-b117929e002d","		private String OtherMember;//":"","		private String MeetingContent;//":"关于OA使用","		private String OwningUser;//":"27a909e1-9fb4-4c67-afcd-3dc9032fdd6b","		private String CreatedBy;//":"27a909e1-9fb4-4c67-afcd-3dc9032fdd6b","		private String CreatedOn;//":"","		private String ModifiedBy;//":"27a909e1-9fb4-4c67-afcd-3dc9032fdd6b","		private String ModifiedOn;//":"2017/3/22 15:49:24"}		public String getValueId() {			return ValueId;		}		public void setValueId(String valueId) {			ValueId = valueId;		}		public String getName() {			return Name;		}		public void setName(String name) {			Name = name;		}		public String getMeetingType() {			return MeetingType;		}		public void setMeetingType(String meetingType) {			MeetingType = meetingType;		}		public String getScheduledStart() {			return ScheduledStart;		}		public void setScheduledStart(String scheduledStart) {			ScheduledStart = scheduledStart;		}		public String getScheduledEnd() {			return ScheduledEnd;		}		public void setScheduledEnd(String scheduledEnd) {			ScheduledEnd = scheduledEnd;		}		public String getRimderTime() {			return RimderTime;		}		public void setRimderTime(String rimderTime) {			RimderTime = rimderTime;		}		public String getRoomId() {			return RoomId;		}		public void setRoomId(String roomId) {			RoomId = roomId;		}		public String getLocation() {			return Location;		}		public void setLocation(String location) {			Location = location;		}		public String getInviteQty() {			return InviteQty;		}		public void setInviteQty(String inviteQty) {			InviteQty = inviteQty;		}		public String getJoinQty() {			return JoinQty;		}		public void setJoinQty(String joinQty) {			JoinQty = joinQty;		}		public String getStatusCode() {			return StatusCode;		}		public void setStatusCode(String statusCode) {			StatusCode = statusCode;		}		public String getHostName() {			return HostName;		}		public void setHostName(String hostName) {			HostName = hostName;		}		public String getContactUser() {			return ContactUser;		}		public void setContactUser(String contactUser) {			ContactUser = contactUser;		}		public String getOtherMember() {			return OtherMember;		}		public void setOtherMember(String otherMember) {			OtherMember = otherMember;		}		public String getMeetingContent() {			return MeetingContent;		}		public void setMeetingContent(String meetingContent) {			MeetingContent = meetingContent;		}		public String getOwningUser() {			return OwningUser;		}		public void setOwningUser(String owningUser) {			OwningUser = owningUser;		}		public String getCreatedBy() {			return CreatedBy;		}		public void setCreatedBy(String createdBy) {			CreatedBy = createdBy;		}		public String getCreatedOn() {			return CreatedOn;		}		public void setCreatedOn(String createdOn) {			CreatedOn = createdOn;		}		public String getModifiedBy() {			return ModifiedBy;		}		public void setModifiedBy(String modifiedBy) {			ModifiedBy = modifiedBy;		}		public String getModifiedOn() {			return ModifiedOn;		}		public void setModifiedOn(String modifiedOn) {			ModifiedOn = modifiedOn;		}		@JSONField(serialize = false)		private long startTime = 0;		@JSONField(serialize = false)		private long endTime = 0;		@JSONField(serialize = false)		public boolean isBetween(long l) {			if (startTime == 0) {				startTime = getTime(ScheduledStart);			}			if (endTime == 0) {				endTime = getTime(ScheduledEnd);			}			if (l >= startTime && l <= endTime) {				return true;			}			return false;		}		@JSONField(serialize = false)		public long getStartTime(){			if (startTime == 0) {				startTime = getTime(ScheduledStart);			}			return startTime;		}		@JSONField(serialize = false)		private static long getTime(String date) {			try {				SimpleDateFormat sdf = new SimpleDateFormat(						"yyyy/M/d H:mm:ss");				Date d = sdf.parse(date);				Calendar ca = Calendar.getInstance();				ca.setTime(d);				ca.set(Calendar.HOUR_OF_DAY, 0);				ca.set(Calendar.MINUTE,0);				ca.set(Calendar.SECOND, 0);				return ca.getTime().getTime();			} catch (ParseException e) {				return 0;			}		}	}}