package cn.com.phinfo.protocol;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;public class NewsAddContentRun extends QuickRunObjectBase {	public NewsAddContentRun(final String id,final String parentid,String comments) {		super(LURLInterface.GET_URL_NEWS_COMMENT_ADD(id,parentid,comments),null,NewsAddContentResultBean.class);	}	public static class NewsAddContentResultBean extends HttpResultBeanBase{		private String commentId;		private String contentId;		private String comment;		private String createdBy;		public String getCommentId() {			return commentId;		}		public void setCommentId(String commentId) {			this.commentId = commentId;		}		public String getContentId() {			return contentId;		}		public void setContentId(String contentId) {			this.contentId = contentId;		}		public String getComment() {			return comment;		}		public void setComment(String comment) {			this.comment = comment;		}		public String getCreatedBy() {			return createdBy;		}		public void setCreatedBy(String createdBy) {			this.createdBy = createdBy;		}	}	}