package cn.com.phinfo.protocol;import java.util.List;import java.util.Stack;import com.alibaba.fastjson.annotation.JSONField;import com.heqifuhou.imgutils.BitmapDataListInstanceUtils;import com.heqifuhou.imgutils.FileItem;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;public class SearchReportRun extends QuickRunObjectBase {	public SearchReportRun(final String worklogtypecode,final String scope,final String search,final int pageNumber,final String owningUser) {		super(LURLInterface.GET_REPORT_SEARCH(worklogtypecode,scope,search,pageNumber,owningUser),null,SearchReportResultBean.class);	}	public static class SearchReportResultBean extends HttpResultBeanBase{		private List<SearchItem> listData = new Stack<SearchItem>();		public List<SearchItem> getListData() {			return listData;		}		public void setListData(List<SearchItem> listData) {			this.listData = listData;		}	}		public static class SearchItem{		private String WorklogId;		private String Location;		private String Name;		private String OwningUser;		private String OwningBusinessUnit;		private String WorklogType;		private String StartDate;		private String EndDate;		private String YearNumber;		private String MonthNumber;		private String WeekNumber;		private String ContentBody;		private String CommentQty;		private String LikeQty;		private boolean IsRead;		private String OwningUserName;		private String CreatedOn;		private String ModifiedOn;		private List<ImgItem> imgs = new Stack<ImgItem>();		public List<ImgItem> getImgs() {			return imgs;		}		public void setImgs(List<ImgItem> imgs) {			this.imgs = imgs;		}		public String getModifiedOn() {			return ModifiedOn;		}		public void setModifiedOn(String modifiedOn) {			ModifiedOn = modifiedOn;		}		public String getCreatedOn() {			return CreatedOn;		}		public void setCreatedOn(String createdOn) {			CreatedOn = createdOn;		}		public String getOwningUserName() {			return OwningUserName;		}		public void setOwningUserName(String owningUserName) {			OwningUserName = owningUserName;		}		public String getWorklogId() {			return WorklogId;		}		public void setWorklogId(String worklogId) {			WorklogId = worklogId;		}		public String getLocation() {			return Location;		}		public void setLocation(String location) {			Location = location;		}		public String getName() {			return Name;		}		public void setName(String name) {			Name = name;		}		public String getOwningUser() {			return OwningUser;		}		public void setOwningUser(String owningUser) {			OwningUser = owningUser;		}		public String getOwningBusinessUnit() {			return OwningBusinessUnit;		}		public void setOwningBusinessUnit(String owningBusinessUnit) {			OwningBusinessUnit = owningBusinessUnit;		}		public String getWorklogType() {			return WorklogType;		}		public void setWorklogType(String worklogType) {			WorklogType = worklogType;		}		public String getStartDate() {			return StartDate;		}		public void setStartDate(String startDate) {			StartDate = startDate;		}		public String getEndDate() {			return EndDate;		}		public void setEndDate(String endDate) {			EndDate = endDate;		}		public String getYearNumber() {			return YearNumber;		}		public void setYearNumber(String yearNumber) {			YearNumber = yearNumber;		}		public String getMonthNumber() {			return MonthNumber;		}		public void setMonthNumber(String monthNumber) {			MonthNumber = monthNumber;		}		public String getWeekNumber() {			return WeekNumber;		}		public void setWeekNumber(String weekNumber) {			WeekNumber = weekNumber;		}		public String getContentBody() {			return ContentBody;		}		public void setContentBody(String contentBody) {			ContentBody = contentBody;		}		public String getCommentQty() {			return CommentQty;		}		public void setCommentQty(String commentQty) {			CommentQty = commentQty;		}		public String getLikeQty() {			return LikeQty;		}		public void setLikeQty(String likeQty) {			LikeQty = likeQty;		}		public boolean getIsRead() {			return IsRead;		}		public void setIsRead(boolean isRead) {			IsRead = isRead;		}		@JSONField(serialize=false)		private List<String> _imgs=null;		@JSONField(serialize=false)		public List<String> getThumbnailPicUrls(){			if(_imgs==null){				_imgs = new Stack<String>();				for(ImgItem it:imgs){					_imgs.add(it.getThumbnailPath());				}			}			return _imgs;		}		@JSONField(serialize=false)		private List<FileItem> _files = null;		@JSONField(serialize=false)		public List<FileItem> getFileItemList(){			if(_files==null){				_files = new Stack<FileItem>();				for(ImgItem it:imgs){					FileItem f = new FileItem();					f.setFileUrl(it.getThumbnailPath());					_files.add(f);				}			}			return _files;		}	}		public static class ImgItem{		private String id;		private String path;		private String thumbnailPath;		public String getId() {			return id;		}		public void setId(String id) {			this.id = id;		}		public String getPath() {			return path;		}		public void setPath(String path) {			this.path = path;		}		public String getThumbnailPath() {			return thumbnailPath;		}		public void setThumbnailPath(String thumbnailPath) {			this.thumbnailPath = thumbnailPath;		}	}}