package cn.com.phinfo.protocol;import java.util.Calendar;import java.util.HashMap;import java.util.IdentityHashMap;import com.baidu.location.BDLocation;import com.heqifuhou.protocolbase.HttpResultBeanBase;import com.heqifuhou.protocolbase.QuickRunObjectBase;import com.heqifuhou.utils.ParamsCheckUtils;public class WorkCheckInRun extends QuickRunObjectBase {	public WorkCheckInRun(final WorkCheckInRequest request){		super(LURLInterface.GET_WORK_CHECKIN(), new HashMap<String, Object>() {			private static final long serialVersionUID = 1L;			{				put("id", request.getId());				put("checkType", request.getCheckType());				put("BuildingName", request.getBuildingName());				put("Location", request.getLocation());				put("CheckTime", request.getCheckTime());				put("Descripiton", request.getDescripiton());				put("abnormal", request.getAbnormal());				put("WifiName", request.getWifiName());				put("WifiAddress", request.getWifiAddress());				put("OnTime",request.getOnTime());				put("Longitude", request.getLongitude());				put("Latitude", request.getLatitude());				put("AbnormalCode", request.getAbnormalCode());			}		}, new IdentityHashMap<String, Object>() {			private static final long serialVersionUID = 1L;			{				put("file", request.getFile());			}		}, WorkCheckInResultBean.class);	}	public static class WorkCheckInResultBean extends HttpResultBeanBase {	}	public static class WorkCheckInRequest {		String id="";		public String getId() {			return id;		}		public void setId(String id) {			this.id = id;		}		String checkType = "";		String BuildingName = "";		String Location = "";		String CheckTime = "";		String Descripiton = "";		String abnormal = "";		String WifiName = "";		String WifiAddress = "";		String Longitude = "";		String Latitude = "";		String file = "";		String OnTime="";		String AbnormalCode="";		public String getAbnormalCode() {			return AbnormalCode;		}		public void setAbnormalCode(String abnormalCode) {			AbnormalCode = abnormalCode;		}		public String getOnTime() {			return OnTime;		}		public void setOnTime(String onTime) {			OnTime = onTime;		}		private WorkCheckInRequest(){					}		public static WorkCheckInRequest init(BDLocation loc){			WorkCheckInRequest request =  new WorkCheckInRequest();			if(!ParamsCheckUtils.isNull(loc.getBuildingName())){				request.setBuildingName(loc.getBuildingName());			}			if(!ParamsCheckUtils.isNull(loc.getAddrStr())){				request.setLocation(loc.getAddrStr());			}			request.setLongitude(String.valueOf(loc.getLongitude()));			request.setLatitude(String.valueOf(loc.getLatitude()));			return request;		}				private String initCheckTime(){			Calendar calendar = Calendar.getInstance();			int y = calendar.get(Calendar.YEAR);			int m = calendar.get(Calendar.MONTH);			int d = calendar.get(Calendar.DAY_OF_MONTH);			int hh = calendar.get(Calendar.HOUR_OF_DAY);			int mm = calendar.get(Calendar.MINUTE);			int ss = calendar.get(Calendar.SECOND);			CheckTime =  String.format("%04d-%02d-%02d %02d:%02d:%02d",y,m+1,d, hh, mm, ss);			return CheckTime;		}		public String getCheckType() {			return checkType;		}		public void setCheckType(String checkType) {			this.checkType = checkType;		}		public String getBuildingName() {			return BuildingName;		}		public void setBuildingName(String buildingName) {			if(!ParamsCheckUtils.isNull(buildingName)){				BuildingName = buildingName;			}		}		public String getLocation() {			return Location;		}		public void setLocation(String location) {			Location = location;		}		public String getCheckTime() {			initCheckTime();			return CheckTime;		}		public String getDescripiton() {			return Descripiton;		}		public void setDescripiton(String descripiton) {			Descripiton = descripiton;		}		public String getAbnormal() {			return abnormal;		}		public void setAbnormal(String abnormal) {			this.abnormal = abnormal;		}		public String getWifiName() {			return WifiName;		}		public void setWifiName(String wifiName) {			WifiName = wifiName;		}		public String getWifiAddress() {			return WifiAddress;		}		public void setWifiAddress(String wifiAddress) {			WifiAddress = wifiAddress;		}		public String getLongitude() {			return Longitude;		}		public void setLongitude(String longitude) {			Longitude = longitude;		}		public String getLatitude() {			return Latitude;		}		public void setLatitude(String latitude) {			Latitude = latitude;		}		public String getFile() {			return file;		}		public void setFile(String file) {			this.file = file;		}	}}