package com.heqifuhou.protocolbase;import com.alibaba.fastjson.JSON;import com.alibaba.fastjson.parser.Feature;public class FastHttpResultParserBase  implements IHttpParserBase {	private Class<? extends HttpResultBeanBase> cls = null;	public FastHttpResultParserBase(Class<? extends HttpResultBeanBase> cls){		this.cls = cls;	}	@Override	public HttpResultBeanBase parserResult(String s) {		if(cls==null){			cls = HttpResultBeanBase.class;		}		try{			return JSON.parseObject(s,cls,Feature.InitStringFieldAsEmpty);		}catch(Exception e){			return   createInstance(cls).initWithStr(s);		}	}		public static <T> T createInstance(Class<T> cls) {        T obj=null;        try {            obj=cls.newInstance();        } catch (Exception e) {            obj=null;        }        return obj;    }}