package com.heqifuhou.sortlistview;import net.sourceforge.pinyin4j.PinyinHelper;import net.sourceforge.pinyin4j.format.HanyuPinyinCaseType;import net.sourceforge.pinyin4j.format.HanyuPinyinOutputFormat;import net.sourceforge.pinyin4j.format.HanyuPinyinToneType;public class HanyuToPinyinUtils {	   /**	    * 汉字转拼音的方法	    * @param name 汉字	    * @return 拼音	    */	    public static  String HanyuToPinyin(String name){	    	String pinyinName = "";	        char[] nameChar = name.toCharArray();	        HanyuPinyinOutputFormat defaultFormat = 	                                           new HanyuPinyinOutputFormat();	        defaultFormat.setCaseType(HanyuPinyinCaseType.LOWERCASE);	        defaultFormat.setToneType(HanyuPinyinToneType.WITHOUT_TONE);	        for (int i = 0; i < nameChar.length; i++) {	            if (nameChar[i] > 128) {	                try {	                	pinyinName += PinyinHelper.toHanyuPinyinStringArray	                                           (nameChar[i], defaultFormat)[0];	                } catch (Exception e) {	                }	            }else{	            	pinyinName += nameChar[i];	            }	        }	        return pinyinName;	    }}