package com.heqifuhou.utils;import android.app.AlertDialog;import android.content.Context;import android.content.DialogInterface;public class AlertDialogUtils {	public static  void showAlert(final Context context,			final android.content.DialogInterface.OnClickListener listener) {		AlertDialog.Builder builder = new AlertDialog.Builder(context);		builder.setMessage("此功能需要您登陆之后才能使用");		builder.setTitle("友情提示");		builder.setPositiveButton("现在登陆",				new android.content.DialogInterface.OnClickListener() {					@Override					public void onClick(DialogInterface dialog, int which) {						dialog.dismiss();						if (listener != null) {							listener.onClick(dialog, which);						}					}				});		builder.setNegativeButton("稍后登陆",				new android.content.DialogInterface.OnClickListener() {					@Override					public void onClick(DialogInterface dialog, int which) {						dialog.dismiss();					}				});		builder.create().show();	}}