package com.heqifuhou.utils;import android.content.Context;import android.util.AttributeSet;import android.view.KeyEvent;import android.widget.MediaController;public class MyMediaController extends MediaController{	private OnBackKeyListener listener = null;	public MyMediaController(Context context, AttributeSet attrs) {		super(context, attrs);	}	public MyMediaController(Context context) {		super(context);	}	public MyMediaController(Context context, boolean useFastForward) {		super(context, useFastForward);	}	@Override    public void hide() {        super.show();    }	public void setOnBackKeyListener(OnBackKeyListener listener){		this.listener = listener;	}	@Override	public boolean dispatchKeyEvent(KeyEvent event) {		if (event.getKeyCode() == KeyEvent.KEYCODE_BACK) {			if(this.listener!=null){				this.listener.onBackKey();			}			return true;		}		return super.dispatchKeyEvent(event);	};		public interface OnBackKeyListener{		void onBackKey();	}}