package com.heqifuhou.utils;import android.content.Context;public class PublicMethod {	public static int Dp2Px(Context context, float dp) {		final float scale = context.getResources().getDisplayMetrics().density;		return (int) (dp * scale + 0.5f);	}	public static int Px2Dp(Context context, float px) {		final float scale = context.getResources().getDisplayMetrics().density;		return (int) (px / scale + 0.5f);	}	public static boolean isNull(final String sz) {		if (sz == null || sz.length() <= 0) {			return true;		}		return false;	}	public static boolean isNull(final byte[] b) {		if (b == null || b.length <= 0) {			return true;		}		return false;	}	public static boolean isNull(final Object sz) {		if (sz == null) {			return true;		}		return false;	}	public static boolean isEquals(final String sz1, final String sz2,			boolean bEqualsNull) {		if (bEqualsNull) {			if (sz1 == null && sz2 == null) {				return true;			}		}		if (sz1 != null && sz2 != null) {			return sz1.equals(sz2);		}		return false;	}}