package com.heqifuhou.utils;import java.io.File;import android.app.Activity;import android.content.Intent;import android.net.Uri;import android.os.Environment;import android.provider.MediaStore;import android.widget.Toast;import cn.com.phinfo.oaact.MyApplet;public class TakePhotosUtils {	public static String takePhoto(Activity act,int idCode) {		if(!Environment.getExternalStorageState().equals(Environment.MEDIA_MOUNTED)){			Toast.makeText(act, "请安装TF卡", Toast.LENGTH_LONG).show();			return "";		}		Intent openCameraIntent = new Intent(MediaStore.ACTION_IMAGE_CAPTURE);		File file = new File(Environment.getExternalStorageDirectory()				+ "/"+MyApplet.getInstance().getPackageName()+"/");		if(!file.exists()){			file.mkdirs();		}		file = new File(file.getAbsolutePath(), String.valueOf(System.currentTimeMillis())				+ ".jpg");		String path = file.getPath();		Uri imageUri = Uri.fromFile(file);		openCameraIntent.putExtra(MediaStore.EXTRA_OUTPUT, imageUri);		act.startActivityForResult(openCameraIntent, idCode);		return path;	}}