package com.heqifuhou.view;import android.app.Activity;import android.app.Dialog;import android.os.Bundle;import android.view.View;import android.view.Window;import android.view.View.OnClickListener;import android.widget.TextView;import cn.com.phinfo.oaact.R;public class CheckInSuccessDialog extends Dialog implements OnClickListener{	private TextView date;	private String currDate;	private android.view.View.OnClickListener listener;	public CheckInSuccessDialog(Activity act,String date,android.view.View.OnClickListener l) {		super(act);		this.requestWindowFeature(Window.FEATURE_NO_TITLE); 		this.currDate = date;		this.listener =l;	}		@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		this.setContentView(R.layout.dialog_checkin_success);		date = (TextView) this.findViewById(R.id.date);		this.findViewById(R.id.btn).setOnClickListener(this);		date.setText(this.currDate);	}	@Override	public void onClick(View arg0) {		this.dismiss();		if(listener!=null){			listener.onClick(arg0);		}	}}