package com.heqifuhou.view;import android.app.Activity;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.widget.TextView;import cn.com.phinfo.oaact.R;public class ConfirmDialog extends MyDialog implements OnClickListener {	private Object it;	private OnDialogOKListener l = null;	private String tip;	public ConfirmDialog(Activity act,final String tip, Object it) {		super(act);		this.it = it;		this.tip = tip;	}	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		this.setContentView(R.layout.confirm_dialog);		TextView txt = (TextView) this.findViewById(R.id.tip);		txt.setText(this.tip);		this.findViewById(R.id.cancel)		 .setOnClickListener(this);		this.findViewById(R.id.ok)		 .setOnClickListener(this);	}	@Override	public void onClick(View arg0) {		switch (arg0.getId()) {		case R.id.cancel:			break;		case R.id.ok:			if (l != null) {				l.onOKItem(it);			}			break;		default:			break;		}		dismiss();	}	public void setOnDialogOKListener(OnDialogOKListener l) {		this.l = l;	}	public interface OnDialogOKListener {		void onOKItem(Object obj);	}}