package com.heqifuhou.view;import android.app.Activity;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.widget.EditText;import cn.com.phinfo.oaact.R;public class EditDialog extends MyDialog implements OnClickListener {	private Object it;	private OnDialogEditOKListener l = null;	private EditText editText;	public EditDialog(Activity act, Object it) {		super(act);		this.it = it;	}	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		this.setContentView(R.layout.edit_dialog);		editText = (EditText) this.findViewById(R.id.tip);		this.findViewById(R.id.cancel)		 .setOnClickListener(this);		this.findViewById(R.id.ok)		 .setOnClickListener(this);	}	@Override	public void onClick(View arg0) {		switch (arg0.getId()) {		case R.id.cancel:			break;		case R.id.ok:			if (l != null) {				l.onEditOKItem(editText.getText().toString(),it);			}			break;		default:			break;		}		dismiss();	}	public void setOnDialogEditOKListener(OnDialogEditOKListener l) {		this.l = l;	}	public interface OnDialogEditOKListener {		void onEditOKItem(String txt,Object obj);	}}