package com.heqifuhou.view;import android.content.Context;import android.util.AttributeSet;import android.view.MotionEvent;public class NoScrollTouchGridView extends NoScrollGridView {     public NoScrollTouchGridView(Context context, AttributeSet attrs) {         super(context, attrs);     }     public NoScrollTouchGridView(Context context) {         super(context);     }     public NoScrollTouchGridView(Context context, AttributeSet attrs, int defStyle) {         super(context, attrs, defStyle);     }     @Override      public boolean dispatchTouchEvent(MotionEvent ev) {          if(ev.getAction() == MotionEvent.ACTION_MOVE){                   return true;             }            return super.dispatchTouchEvent(ev);      } }