package com.heqifuhou.view;import com.heqifuhou.utils.Utils;import android.app.Activity;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import android.view.ViewGroup.LayoutParams;import android.widget.Button;import android.widget.TextView;import cn.com.phinfo.oaact.R;public class PopupDialog extends MyDialog implements OnClickListener {	private Object it;	private String[] ItemList;	private OnDialogItemListener l = null;	private Activity act;	public PopupDialog(Activity act, Object it, String[] ItemList) {		super(act);		this.act = act;		this.it = it;		this.ItemList = ItemList;	}	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		this.setContentView(R.layout.dialog_item_popupwindows);		ViewGroup li = (ViewGroup) this.findViewById(R.id.li_popup);		for (int i = 0; i < ItemList.length; i++) {			if (i > 0) {				TextView v = new TextView(act);				v.setBackgroundColor(0xFFf3f3f3);				li.addView(v,LayoutParams.FILL_PARENT,Utils.dip2px(act,1));			}			Button child = new Button(act);			child.setTextColor(0xFF585858);			child.setTextSize(18);			child.setText(ItemList[i]);			child.setBackgroundResource(R.drawable.bt_nobgd);			child.setOnClickListener(this);		    child.setTag(i);			li.addView(child, LayoutParams.FILL_PARENT,Utils.dip2px(act,								 55));		}		this.findViewById(R.id.item_popupwindows_cancel)		 .setOnClickListener(this);			}	@Override	public void onClick(View arg0) {		switch (arg0.getId()) {		case R.id.item_popupwindows_cancel:			break;		default:			if (l != null) {				int pos = (Integer)arg0.getTag();				l.onDialogItem(pos , it);			}			break;		}		dismiss();	}	public void setOnDialogItemListener(OnDialogItemListener l) {		this.l = l;	}	public interface OnDialogItemListener {		void onDialogItem(int pos, Object obj);	}}