package com.heqifuhou.viewflow;import android.content.Context;import android.util.AttributeSet;import android.view.MotionEvent;import android.widget.HorizontalScrollView;public class MyViewFlow extends ViewFlow {    private HorizontalScrollView mHorizontalScrollView;    public MyViewFlow(Context context, AttributeSet attrs) {        super(context, attrs);    }    public void setHorizontalScrollView(HorizontalScrollView viewPager) {        mHorizontalScrollView = viewPager;    }    @Override    public boolean onInterceptTouchEvent(MotionEvent ev) {        if (mHorizontalScrollView != null)            switch (ev.getAction()) {                case MotionEvent.ACTION_DOWN:                    mHorizontalScrollView.requestDisallowInterceptTouchEvent(true);                    break;                case MotionEvent.ACTION_UP:                    mHorizontalScrollView.requestDisallowInterceptTouchEvent(false);                    break;                case MotionEvent.ACTION_CANCEL:                    mHorizontalScrollView.requestDisallowInterceptTouchEvent(false);                    break;                case MotionEvent.ACTION_MOVE:                    mHorizontalScrollView.requestDisallowInterceptTouchEvent(true);                    break;            }        return super.onInterceptTouchEvent(ev);    }}